                    let productOfLDPnew = new productOfLDP(producto._id, producto.productName, producto.categoriaRubro.categoryName, producto.codigo, producto.stock, producto.ultimoRegCompra.fechaDeCompra, producto.ultimoRegCompra.precioCompraUnitario, producto.precioVenta);
